param(
    [string]$VMIdsBase64,
    [bool]$MonitorAllVMs,
    [string]$DCRId
)

$ErrorActionPreference = 'Stop'

Write-Host "========================================"
Write-Host "Azure VM Monitoring Setup"
Write-Host "========================================"
Write-Host "Monitor All VMs: $MonitorAllVMs"
Write-Host "DCR ID: $DCRId"
Write-Host ""

# Wait for role propagation
Write-Host "Waiting 60 seconds for role assignment propagation..."
Start-Sleep -Seconds 60

$vmArray = @()

if ($MonitorAllVMs) {
    Write-Host "Discovering all Azure VMs in subscription..."
    try {
        $allVMs = Get-AzVM -ErrorAction Stop
        Write-Host "Found $($allVMs.Count) Azure VMs"

        foreach ($vm in $allVMs) {
            $vmArray += $vm.Id
            Write-Host "  - $($vm.Name)"
        }
    }
    catch {
        Write-Host "ERROR: $_" -ForegroundColor Red
        exit 1
    }
}
else {
    Write-Host "Decoding VM IDs from Base64..."

    try {
        $VMIdsJson = [System.Text.Encoding]::UTF8.GetString([System.Convert]::FromBase64String($VMIdsBase64))
        Write-Host "Decoded JSON: $VMIdsJson"

        if ([string]::IsNullOrWhiteSpace($VMIdsJson) -or $VMIdsJson -eq '[]') {
            Write-Host "ERROR: No VMs provided!" -ForegroundColor Red
            exit 1
        }

        $vmArray = $VMIdsJson | ConvertFrom-Json
        Write-Host "Parsed $($vmArray.Count) VM(s)"
    }
    catch {
        Write-Host "ERROR: Failed to decode/parse: $_" -ForegroundColor Red
        exit 1
    }
}

if ($vmArray.Count -eq 0) {
    Write-Host "No Azure VMs to process" -ForegroundColor Yellow
    $DeploymentScriptOutputs = @{}
    $DeploymentScriptOutputs['status'] = 'NoVMs'
    exit 0
}

Write-Host ""
Write-Host "Processing $($vmArray.Count) Azure VM(s)..."
Write-Host ""

$results = @()
$successCount = 0
$partialCount = 0
$failCount = 0

foreach ($vmId in $vmArray) {
    Write-Host "========================================"
    Write-Host "Processing: $vmId" -ForegroundColor Cyan
    Write-Host "========================================"

    $vmName = $vmId.Split('/')[-1]
    $vmRG = $vmId.Split('/')[4]

    Write-Host "VM Name: $vmName"
    Write-Host "Resource Group: $vmRG"

    $amaInstalled = $false
    $dcrAssociated = $false
    $vmRunning = $false
    $vmStatus = "Unknown"
    $errorMessage = ""

    try {
        # Get VM details
        $vm = Get-AzVM -ResourceGroupName $vmRG -Name $vmName -ErrorAction Stop
        $osType = $vm.StorageProfile.OsDisk.OsType
        $location = $vm.Location

        # Get VM status
        $vmStatusObj = Get-AzVM -ResourceGroupName $vmRG -Name $vmName -Status -ErrorAction Stop
        $powerState = $vmStatusObj.Statuses | Where-Object { $_.Code -like 'PowerState/*' } | Select-Object -First 1
        $vmStatus = if ($powerState) { $powerState.Code.Replace('PowerState/', '') } else { 'unknown' }
        $vmRunning = ($vmStatus -eq 'running')

        Write-Host "OS Type: $osType"
        Write-Host "Location: $location"
        Write-Host "Status: $vmStatus" -ForegroundColor $(if ($vmRunning) { 'Green' } else { 'Yellow' })
        Write-Host ""

        # Determine extension details
        if ($osType -eq "Linux") {
            $extensionName = "AzureMonitorLinuxAgent"
            $extensionPublisher = "Microsoft.Azure.Monitor"
            $extensionType = "AzureMonitorLinuxAgent"
            $extensionVersion = "1.28"
        }
        else {
            $extensionName = "AzureMonitorWindowsAgent"
            $extensionPublisher = "Microsoft.Azure.Monitor"
            $extensionType = "AzureMonitorWindowsAgent"
            $extensionVersion = "1.22"
        }

        # Step 1: Check and install AMA (only if VM is running)
        Write-Host "[1/2] Azure Monitor Agent..." -ForegroundColor Yellow

        if ($vmRunning) {
            $existingExtension = Get-AzVMExtension `
                -ResourceGroupName $vmRG `
                -VMName $vmName `
                -Name $extensionName `
                -ErrorAction SilentlyContinue

            if ($existingExtension) {
                Write-Host "  ✓ AMA already installed (v$($existingExtension.TypeHandlerVersion))" -ForegroundColor Green
                $amaInstalled = $true
            }
            else {
                Write-Host "  Installing Azure Monitor Agent..." -ForegroundColor Cyan

                try {
                    $extensionResult = Set-AzVMExtension `
                        -ResourceGroupName $vmRG `
                        -VMName $vmName `
                        -Name $extensionName `
                        -Publisher $extensionPublisher `
                        -ExtensionType $extensionType `
                        -TypeHandlerVersion $extensionVersion `
                        -EnableAutomaticUpgrade $true `
                        -Location $location `
                        -ErrorAction Stop

                    Write-Host "  ✓ AMA installed successfully" -ForegroundColor Green
                    $amaInstalled = $true
                }
                catch {
                    Write-Host "  ✗ Failed to install AMA: $($_.Exception.Message)" -ForegroundColor Red
                    $errorMessage = "AMA: $($_.Exception.Message)"
                    $amaInstalled = $false
                }
            }
        }
        else {
            Write-Host "  ⚠ VM is stopped - AMA will be installed when VM starts" -ForegroundColor Yellow
            $amaInstalled = $false
        }

        Write-Host ""

        # Step 2: Associate DCR (works even if VM is stopped)
        Write-Host "[2/2] Data Collection Rule Association..." -ForegroundColor Yellow

        $associationName = "dcr-association-$vmName"

        try {
            $existingAssociation = Get-AzDataCollectionRuleAssociation `
                -TargetResourceId $vmId `
                -AssociationName $associationName `
                -ErrorAction SilentlyContinue

            if ($existingAssociation) {
                Write-Host "  ✓ DCR association already exists" -ForegroundColor Green
                $dcrAssociated = $true
            }
            else {
                $association = New-AzDataCollectionRuleAssociation `
                    -TargetResourceId $vmId `
                    -AssociationName $associationName `
                    -DataCollectionRuleId $DCRId `
                    -ErrorAction Stop

                Write-Host "  ✓ DCR associated successfully" -ForegroundColor Green
                $dcrAssociated = $true
            }
        }
        catch {
            Write-Host "  ✗ Failed to associate DCR: $($_.Exception.Message)" -ForegroundColor Red
            if ($errorMessage) {
                $errorMessage += "; DCR: $($_.Exception.Message)"
            } else {
                $errorMessage = "DCR: $($_.Exception.Message)"
            }
            $dcrAssociated = $false
        }

        Write-Host ""

        # Determine overall status
        if ($dcrAssociated) {
            if ($amaInstalled -or -not $vmRunning) {
                Write-Host "✓ Azure VM configured successfully!" -ForegroundColor Green
                if (-not $vmRunning) {
                    Write-Host "  Note: Start the VM to complete AMA installation" -ForegroundColor Cyan
                }
                $successCount++
                $status = "Success"
            }
            else {
                Write-Host "⚠ Partially configured (DCR OK, AMA failed)" -ForegroundColor Yellow
                $partialCount++
                $status = "Partial"
            }
        }
        else {
            Write-Host "✗ Configuration failed" -ForegroundColor Red
            $failCount++
            $status = "Failed"
        }

        Write-Host ""

        $results += @{
            VMName = $vmName
            Status = $status
            OSType = $osType
            VMStatus = $vmStatus
            AMAInstalled = $amaInstalled
            DCRAssociated = $dcrAssociated
            Error = $errorMessage
        }
    }
    catch {
        Write-Host ""
        Write-Host "✗ FAILED: $($_.Exception.Message)" -ForegroundColor Red
        Write-Host ""

        $failCount++

        $results += @{
            VMName = $vmName
            Status = "Failed"
            Error = $_.Exception.Message
        }
    }
}

Write-Host "========================================"
Write-Host "SUMMARY"
Write-Host "========================================"
Write-Host "Total Azure VMs: $($vmArray.Count)"
Write-Host "✓ Fully Configured: $successCount" -ForegroundColor Green
if ($partialCount -gt 0) {
    Write-Host "⚠ Partially Configured: $partialCount (DCR OK, AMA pending)" -ForegroundColor Yellow
}
if ($failCount -gt 0) {
    Write-Host "✗ Failed: $failCount" -ForegroundColor Red
}
Write-Host "========================================"
Write-Host ""

if ($successCount -gt 0 -or $partialCount -gt 0) {
    Write-Host "DCR associations are active on $(($successCount + $partialCount)) Azure VM(s)" -ForegroundColor Green
    if ($partialCount -gt 0) {
        Write-Host "Start stopped VMs to complete AMA installation" -ForegroundColor Cyan
    }
    Write-Host "Data will start appearing in Log Analytics within 5-10 minutes after VMs are running" -ForegroundColor Cyan
}

$DeploymentScriptOutputs = @{}
$DeploymentScriptOutputs['results'] = $results | ConvertTo-Json -Compress
$DeploymentScriptOutputs['successCount'] = $successCount
$DeploymentScriptOutputs['partialCount'] = $partialCount
$DeploymentScriptOutputs['failCount'] = $failCount
$DeploymentScriptOutputs['totalVMs'] = $vmArray.Count
$DeploymentScriptOutputs['status'] = 'Completed'

param(
    [string]$ArcServerIdsBase64,
    [bool]$MonitorAllArcServers,
    [string]$DCRId
)

$ErrorActionPreference = 'Stop'

Write-Host "========================================"
Write-Host "Arc Server Monitoring Setup"
Write-Host "========================================"
Write-Host "Monitor All Arc Servers: $MonitorAllArcServers"
Write-Host "DCR ID: $DCRId"
Write-Host ""

# Install Az.ConnectedMachine module
Write-Host "Installing Az.ConnectedMachine module..." -ForegroundColor Cyan
try {
    if (-not (Get-Module -ListAvailable -Name Az.ConnectedMachine)) {
        Install-Module -Name Az.ConnectedMachine -Force -AllowClobber -Scope CurrentUser -ErrorAction Stop
        Write-Host "✓ Az.ConnectedMachine module installed successfully" -ForegroundColor Green
    } else {
        Write-Host "✓ Az.ConnectedMachine module already installed" -ForegroundColor Green
    }
    Import-Module Az.ConnectedMachine -ErrorAction Stop
    Write-Host "✓ Az.ConnectedMachine module imported" -ForegroundColor Green
}
catch {
    Write-Host "ERROR: Failed to install/import Az.ConnectedMachine module: $_" -ForegroundColor Red
    Write-Host "This module is required to manage Arc-enabled servers" -ForegroundColor Red
    exit 1
}
Write-Host ""

# Wait for role propagation
Write-Host "Waiting 60 seconds for role assignment propagation..."
Start-Sleep -Seconds 60

$arcArray = @()

if ($MonitorAllArcServers) {
    Write-Host "Discovering all Arc-enabled servers in subscription..."
    try {
        $allArcServers = Get-AzConnectedMachine -ErrorAction Stop
        Write-Host "Found $($allArcServers.Count) Arc-enabled servers"

        foreach ($arc in $allArcServers) {
            $arcArray += $arc.Id
            Write-Host "  - $($arc.Name)"
        }
    }
    catch {
        Write-Host "ERROR: $_" -ForegroundColor Red
        exit 1
    }
}
else {
    Write-Host "Decoding Arc Server IDs from Base64..."

    try {
        $ArcIdsJson = [System.Text.Encoding]::UTF8.GetString([System.Convert]::FromBase64String($ArcServerIdsBase64))
        Write-Host "Decoded JSON: $ArcIdsJson"

        if ([string]::IsNullOrWhiteSpace($ArcIdsJson) -or $ArcIdsJson -eq '[]') {
            Write-Host "ERROR: No Arc servers provided!" -ForegroundColor Red
            exit 1
        }

        $arcArray = $ArcIdsJson | ConvertFrom-Json
        Write-Host "Parsed $($arcArray.Count) Arc server(s)"
    }
    catch {
        Write-Host "ERROR: Failed to decode/parse: $_" -ForegroundColor Red
        exit 1
    }
}

if ($arcArray.Count -eq 0) {
    Write-Host "No Arc servers to process" -ForegroundColor Yellow
    $DeploymentScriptOutputs = @{}
    $DeploymentScriptOutputs['status'] = 'NoArcServers'
    exit 0
}

Write-Host ""
Write-Host "Processing $($arcArray.Count) Arc server(s)..."
Write-Host ""

$results = @()
$successCount = 0
$partialCount = 0
$failCount = 0

foreach ($arcId in $arcArray) {
    Write-Host "========================================"
    Write-Host "Processing: $arcId" -ForegroundColor Cyan
    Write-Host "========================================"

    $arcName = $arcId.Split('/')[-1]
    $arcRG = $arcId.Split('/')[4]

    Write-Host "Arc Server Name: $arcName"
    Write-Host "Resource Group: $arcRG"

    $amaInstalled = $false
    $dcrAssociated = $false
    $arcStatus = "Unknown"
    $errorMessage = ""

    try {
        # Get Arc machine details
        $arc = Get-AzConnectedMachine -ResourceGroupName $arcRG -Name $arcName -ErrorAction Stop
        $osType = $arc.OSName
        $location = $arc.Location
        $arcStatus = $arc.Status

        Write-Host "OS Type: $osType"
        Write-Host "Location: $location"
        Write-Host "Status: $arcStatus" -ForegroundColor $(if ($arcStatus -eq 'Connected') { 'Green' } else { 'Yellow' })
        Write-Host ""

        # Determine extension details
        if ($osType -like "*Linux*") {
            $extensionName = "AzureMonitorLinuxAgent"
            $extensionPublisher = "Microsoft.Azure.Monitor"
            $extensionType = "AzureMonitorLinuxAgent"
        }
        else {
            $extensionName = "AzureMonitorWindowsAgent"
            $extensionPublisher = "Microsoft.Azure.Monitor"
            $extensionType = "AzureMonitorWindowsAgent"
        }

        # Step 1: Check and install AMA
        Write-Host "[1/2] Azure Monitor Agent..." -ForegroundColor Yellow

        if ($arcStatus -eq 'Connected') {
            $existingExtension = Get-AzConnectedMachineExtension `
                -ResourceGroupName $arcRG `
                -MachineName $arcName `
                -Name $extensionName `
                -ErrorAction SilentlyContinue

            if ($existingExtension) {
                Write-Host "  ✓ AMA already installed" -ForegroundColor Green
                $amaInstalled = $true
            }
            else {
                Write-Host "  Installing Azure Monitor Agent..." -ForegroundColor Cyan

                try {
                    $extensionResult = New-AzConnectedMachineExtension `
                        -ResourceGroupName $arcRG `
                        -MachineName $arcName `
                        -Name $extensionName `
                        -Publisher $extensionPublisher `
                        -ExtensionType $extensionType `
                        -Location $location `
                        -EnableAutomaticUpgrade `
                        -ErrorAction Stop

                    Write-Host "  ✓ AMA installed successfully" -ForegroundColor Green
                    $amaInstalled = $true
                }
                catch {
                    Write-Host "  ✗ Failed to install AMA: $($_.Exception.Message)" -ForegroundColor Red
                    $errorMessage = "AMA: $($_.Exception.Message)"
                    $amaInstalled = $false
                }
            }
        }
        else {
            Write-Host "  ⚠ Arc server is not connected" -ForegroundColor Yellow
            $amaInstalled = $false
        }

        Write-Host ""

        # Step 2: Associate DCR
        Write-Host "[2/2] Data Collection Rule Association..." -ForegroundColor Yellow

        $associationName = "dcr-association-$arcName"

        try {
            $existingAssociation = Get-AzDataCollectionRuleAssociation `
                -TargetResourceId $arcId `
                -AssociationName $associationName `
                -ErrorAction SilentlyContinue

            if ($existingAssociation) {
                Write-Host "  ✓ DCR association already exists" -ForegroundColor Green
                $dcrAssociated = $true
            }
            else {
                $association = New-AzDataCollectionRuleAssociation `
                    -TargetResourceId $arcId `
                    -AssociationName $associationName `
                    -DataCollectionRuleId $DCRId `
                    -ErrorAction Stop

                Write-Host "  ✓ DCR associated successfully" -ForegroundColor Green
                $dcrAssociated = $true
            }
        }
        catch {
            Write-Host "  ✗ Failed to associate DCR: $($_.Exception.Message)" -ForegroundColor Red
            if ($errorMessage) {
                $errorMessage += "; DCR: $($_.Exception.Message)"
            } else {
                $errorMessage = "DCR: $($_.Exception.Message)"
            }
            $dcrAssociated = $false
        }

        Write-Host ""

        # Determine overall status
        if ($dcrAssociated) {
            if ($amaInstalled -or $arcStatus -ne 'Connected') {
                Write-Host "✓ Arc server configured successfully!" -ForegroundColor Green
                if ($arcStatus -ne 'Connected') {
                    Write-Host "  Note: Reconnect Arc server to complete AMA installation" -ForegroundColor Cyan
                }
                $successCount++
                $status = "Success"
            }
            else {
                Write-Host "⚠ Partially configured (DCR OK, AMA failed)" -ForegroundColor Yellow
                $partialCount++
                $status = "Partial"
            }
        }
        else {
            Write-Host "✗ Configuration failed" -ForegroundColor Red
            $failCount++
            $status = "Failed"
        }

        Write-Host ""

        $results += @{
            ArcServerName = $arcName
            Status = $status
            OSType = $osType
            ArcStatus = $arcStatus
            AMAInstalled = $amaInstalled
            DCRAssociated = $dcrAssociated
            Error = $errorMessage
        }
    }
    catch {
        Write-Host ""
        Write-Host "✗ FAILED: $($_.Exception.Message)" -ForegroundColor Red
        Write-Host ""

        $failCount++

        $results += @{
            ArcServerName = $arcName
            Status = "Failed"
            Error = $_.Exception.Message
        }
    }
}

Write-Host "========================================"
Write-Host "SUMMARY"
Write-Host "========================================"
Write-Host "Total Arc Servers: $($arcArray.Count)"
Write-Host "✓ Fully Configured: $successCount" -ForegroundColor Green
if ($partialCount -gt 0) {
    Write-Host "⚠ Partially Configured: $partialCount (DCR OK, AMA pending)" -ForegroundColor Yellow
}
if ($failCount -gt 0) {
    Write-Host "✗ Failed: $failCount" -ForegroundColor Red
}
Write-Host "========================================"
Write-Host ""

if ($successCount -gt 0 -or $partialCount -gt 0) {
    Write-Host "DCR associations are active on $(($successCount + $partialCount)) Arc server(s)" -ForegroundColor Green
    if ($partialCount -gt 0) {
        Write-Host "Reconnect Arc servers to complete AMA installation" -ForegroundColor Cyan
    }
    Write-Host "Data will start appearing in Log Analytics within 5-10 minutes" -ForegroundColor Cyan
}

$DeploymentScriptOutputs = @{}
$DeploymentScriptOutputs['results'] = $results | ConvertTo-Json -Compress
$DeploymentScriptOutputs['successCount'] = $successCount
$DeploymentScriptOutputs['partialCount'] = $partialCount
$DeploymentScriptOutputs['failCount'] = $failCount
$DeploymentScriptOutputs['totalArcServers'] = $arcArray.Count
$DeploymentScriptOutputs['status'] = 'Completed'
