content.query:
let HealthyThreshold = 80;
let WarningThreshold = 85;
let CriticalThreshold = 90;
let HeartbeatData = Heartbeat
| where TimeGenerated {TimeRange}
| where '*' in ({VMFilter}) or Computer in ({VMFilter})
| summarize LastHeartbeat = max(TimeGenerated) by Computer
| extend MinutesSinceLastHeartbeat = datetime_diff('minute', now(), LastHeartbeat)
| extend Status = case(
    MinutesSinceLastHeartbeat > 10, 'Offline',
    'Online'
);
let CPUData = Perf
| where TimeGenerated {TimeRange}
| where ObjectName == 'Processor' and CounterName == '% Processor Time'
| where InstanceName == '_Total'
| where '*' in ({VMFilter}) or Computer in ({VMFilter})
| summarize CPUUsage = avg(CounterValue) by Computer;
let MemoryDataWindows = Perf
| where TimeGenerated {TimeRange}
| where ObjectName == 'Memory' and CounterName == '% Committed Bytes In Use'
| where '*' in ({VMFilter}) or Computer in ({VMFilter})
| summarize MemoryUsage = avg(CounterValue) by Computer;
let MemoryDataLinux = Perf
| where TimeGenerated {TimeRange}
| where ObjectName == 'Memory' and CounterName == 'Available MBytes'
| where '*' in ({VMFilter}) or Computer in ({VMFilter})
| summarize AvailableMemory = avg(CounterValue) by Computer
| join kind=inner (
    Perf
    | where TimeGenerated {TimeRange}
    | where ObjectName == 'Memory' and CounterName == 'Total MBytes'
    | summarize TotalMemory = avg(CounterValue) by Computer
) on Computer
| extend MemoryUsage = 100 - (AvailableMemory / TotalMemory * 100)
| project Computer, MemoryUsage;
let MemoryData = union MemoryDataWindows, MemoryDataLinux
| summarize MemoryUsage = max(MemoryUsage) by Computer;
let DiskData = Perf
| where TimeGenerated {TimeRange}
| where ObjectName == 'LogicalDisk' and CounterName == '% Free Space'
| where InstanceName != '_Total'
| where '*' in ({VMFilter}) or Computer in ({VMFilter})
| summarize DiskFreeSpace = avg(CounterValue) by Computer
| extend DiskUsage = 100 - DiskFreeSpace;
HeartbeatData
| join kind=leftouter CPUData on Computer
| join kind=leftouter MemoryData on Computer
| join kind=leftouter DiskData on Computer
| extend CPUUsage = coalesce(CPUUsage, 0.0)
| extend MemoryUsage = coalesce(MemoryUsage, 0.0)
| extend DiskUsage = coalesce(DiskUsage, 0.0)
| extend OverallStatus = case(
    Status == 'Offline', 'âš« Offline',
    CPUUsage >= CriticalThreshold or MemoryUsage >= CriticalThreshold or DiskUsage >= CriticalThreshold, 'ðŸ”´ Critical',
    CPUUsage >= WarningThreshold or MemoryUsage >= WarningThreshold or DiskUsage >= WarningThreshold, 'ðŸŸ¡ Warning',
    'ðŸŸ¢ Healthy'
)
| project Computer, OverallStatus, CPUUsage, MemoryUsage, DiskUsage, LastHeartbeat, MinutesSinceLastHeartbeat
| order by OverallStatus desc, Computer asc
